CREATE TABLE tblCronQueue ( fldCronQueueID varchar(11) NOT NULL DEFAULT '', fldRef varchar(11) NOT NULL DEFAULT '0', fldCommand varchar(255) NOT NULL DEFAULT '', fldPriority char(3) NOT NULL DEFAULT '0', fldStatus char(10) NOT NULL DEFAULT 'NEW', fldRunTime varchar(30) DEFAULT NULL, fldReturnValue char(3) DEFAULT NULL, fldReturnOutput varchar(255) DEFAULT NULL, PRIMARY KEY (fldCronQueueID) );
CREATE TABLE tblGroup ( fldGroupID varchar(10) NOT NULL DEFAULT '', fldName varchar(50) NOT NULL DEFAULT '', fldLongName varchar(240) NOT NULL DEFAULT '', PRIMARY KEY (fldGroupID));
CREATE TABLE tblUser ( fldUserID varchar(10) NOT NULL DEFAULT '', fldDomain varchar(50) NOT NULL DEFAULT '', fldUser varchar(100) NOT NULL DEFAULT '', fldSalutation varchar(10) DEFAULT NULL, fldFirstName varchar(50) DEFAULT NULL, fldLastName varchar(50) DEFAULT NULL, fldPreferedName varchar(50) DEFAULT NULL, fldPassword varchar(100) NOT NULL DEFAULT '', fldAltDomain varchar(100) DEFAULT NULL, fldLevel char(3) NOT NULL DEFAULT '7', fldCreated varchar(25) NOT NULL DEFAULT '', fldModified varchar(25) NOT NULL DEFAULT '', fldLastLogin varchar(25) NOT NULL DEFAULT '', fldTimeZone varchar(50) NOT NULL DEFAULT '', fldFails char(3) NOT NULL DEFAULT '0', PRIMARY KEY (fldUserID));
CREATE TABLE tblNextNumber ( fldNextNumberID char(10) NOT NULL DEFAULT '', fldTable char(50) NOT NULL DEFAULT '', fldNext int(6) NOT NULL DEFAULT '0', fldFormat char(10) NOT NULL DEFAULT '', fldComment char(100) NOT NULL DEFAULT '', fldTableChecksum char(50) DEFAULT NULL, PRIMARY KEY (fldNextNumberID));
CREATE TABLE tblFileCheck ( fldFileCheckID char(10) NOT NULL DEFAULT '', fldFileName char(255) NOT NULL DEFAULT '', fldSize char(20) NOT NULL DEFAULT '', fldCRC char(50) NOT NULL DEFAULT 'DATA', PRIMARY KEY (fldFileCheckID));
CREATE TABLE tblSecPrivileges ( fldSecPrivilegesID varchar(10) NOT NULL DEFAULT '0', fldName varchar(50) DEFAULT NULL, fldAction varchar(50) DEFAULT NULL, PRIMARY KEY (fldSecPrivilegesID));
CREATE TABLE tblCrossSiteProtection ( fldCrossSiteProtectionID varchar(12) NOT NULL DEFAULT '', fldUniqueID varchar(50) DEFAULT NULL, fldExpiryDate int(10) DEFAULT NULL, PRIMARY KEY (fldCrossSiteProtectionID));
CREATE TABLE tblScheduler ( fldSchedulerID varchar(11) NOT NULL DEFAULT '', fldCommand varchar(255) NOT NULL DEFAULT '', fldActive char(3) NOT NULL DEFAULT 'Yes', fldStartTime varchar(40) NOT NULL DEFAULT '', fldCron varchar(100) NOT NULL DEFAULT '', fldLastRun varchar(40) NOT NULL DEFAULT '', PRIMARY KEY (fldSchedulerID));
CREATE TABLE tblUserType ( fldUserTypeID varchar(20) NOT NULL DEFAULT '', fldUserTypeValue char(3) NOT NULL DEFAULT '', fldUserTypeName varchar(20) NOT NULL DEFAULT '');
CREATE TABLE tblLoginAttempt ( fldLoginAttemptID varchar(12) NOT NULL DEFAULT '', fldUsername varchar(100) DEFAULT NULL, fldPassword varchar(100) DEFAULT NULL, fldAgent varchar(200) DEFAULT NULL, fldRemoteAddress varchar(100) DEFAULT NULL, PRIMARY KEY (fldLoginAttemptID));
CREATE TABLE tblTimeZone ( fldTimeZoneID char(10) NOT NULL, fldDescription char(50) NOT NULL DEFAULT '', fldTime char(20) NOT NULL DEFAULT '', fldCode char(10) NOT NULL DEFAULT '', PRIMARY KEY (fldTimeZoneID));
CREATE TABLE tblUserGroupMap ( fldUserGroupMapID char(10) NOT NULL DEFAULT '', fldUserID char(10) NOT NULL DEFAULT '', fldGroupID char(10) NOT NULL DEFAULT '', PRIMARY KEY (fldUserGroupMapID));
CREATE TABLE tblSecPrivUserMap ( fldSecPrivUserMapID char(10) NOT NULL DEFAULT '', fldUserID char(10) NOT NULL DEFAULT '', fldGroupID char(10) NOT NULL DEFAULT '', fldLevelID char(10) NOT NULL DEFAULT '', fldPrivilegeID char(10) NOT NULL DEFAULT '', fldStartDate int(25) DEFAULT '0', fldEndDate int(25) DEFAULT '0', fldLoginAction varchar(100) NOT NULL DEFAULT '', PRIMARY KEY (fldSecPrivUserMapID));
CREATE TABLE tblConfig (fldConfigID varchar(11) NOT NULL,fldUserID varchar (11) NOT NULL, fldKey varchar (100) NOT NULL, fldValue varchar (1000) DEFAULT NULL, PRIMARY KEY (fldConfigID));
CREATE TABLE tblMigration (fldMigrationID varchar (11) NOT NULL, fldRun char (5) NOT NULL, fldClass varchar (200) NOT NULL, fldMethod varchar (50) NOT NULL, fldReadTime datetime NOT NULL DEFAULT current_timestamp, PRIMARY KEY (fldMigrationID));





CREATE INDEX tblNextNumber_fldTable ON tblNextNumber (fldTable);
CREATE INDEX tblFileCheck_fldFileName ON tblFileCheck (fldFileName);
CREATE INDEX tblCrossSiteProtection_fldUniqueID_idx ON tblCrossSiteProtection (fldUniqueID);
CREATE INDEX tblCrossSiteProtection_fldExpiryDate_idx ON tblCrossSiteProtection (fldExpiryDate);
CREATE INDEX tblUser_fldDomain ON tblUser (fldDomain);
